		if (pArray == null)
		{
			pArray = new CArrayList();
			pHo.hoBaseNoRepeat = pArray;
		}
		else
		{
			for (n = 0; n < pArray.size(); n++)
			{
				if (pArray.get(n) == identifier)
					return false;
			}
		}
		pArray.add(identifier);

		pArray = pHo.hoPrevNoRepeat;
