	CRun.compareTo = function (pValue1, pValue2, comp)
	{
		switch (comp)
		{
			case 0:	// COMPARE_EQ:
				return pValue1 == pValue2;
			case 1:	// COMPARE_NE:
				return pValue1 != pValue2;
			case 2:	// COMPARE_LE:
				return pValue1 <= pValue2;
			case 3:	// COMPARE_LT:
				return pValue1 < pValue2;
			case 4:	// COMPARE_GE:
				return pValue1 >= pValue2;
			case 5:	// COMPARE_GT:
				return pValue1 > pValue2;
		}
		return false;
	}

	CRun.compareTer = function (value1, value2, comparaison)
	{
		switch (comparaison)
		{
			case 0:	// COMPARE_EQ:
				return (value1 == value2);
			case 1:	// COMPARE_NE:
				return (value1 != value2);
			case 2:	// COMPARE_LE:
				return (value1 <= value2);
			case 3:	// COMPARE_LT:
				return (value1 < value2);
			case 4:	// COMPARE_GE:
				return (value1 >= value2);
			case 5:	// COMPARE_GT:
				return (value1 > value2);
		}
		return false;
	}

