	CRun.get_DirFromPente = function (x, y)
	{
		if (x == 0)
		{
			if (y >= 0) return 24;
			return 8;
		}
		if (y == 0)
		{
			if (x >= 0) return 0;
			return 16;
		}

		var dir;
		var flagX = false;
		var flagY = false;
		if (x < 0)
		{
			flagX = true;
			x = -x;
		}
		if (y < 0)
		{
			flagY = true;
			y = -y;
		}

		var d = (x * 256) / y;
		var index;
		for (index = 0; ; index += 2)
		{
			if (d >= CMove.CosSurSin32[index])
				break;
		}
		dir = CMove.CosSurSin32[index + 1];

		if (flagY)
		{
			dir = -dir + 32;
			dir &= 31;
		}
		if (flagX)
		{
			dir -= 8;
			dir &= 31;
			dir = -dir;
			dir &= 31;
			dir += 8;
			dir &= 31;
		}
		return dir;
	}


