		losCompare: function (x1, y1, x2, y2, pHo)
		{
			var delta;
			var x, y;

			var xLeft = pHo.hoX - pHo.hoImgXSpot;
			var xRight = xLeft + pHo.hoImgWidth;
			var yTop = pHo.hoY - pHo.hoImgYSpot;
			var yBottom = yTop + pHo.hoImgHeight;

			if (x2 - x1 > y2 - y1)
			{
				delta = (y2 - y1) / (x2 - x1);
				if (x2 > x1)
				{
					if (xRight < x1 || xLeft >= x2)
					{
						return false;
					}
				}
				else
				{
					if (xRight < x2 || xLeft >= x1)
					{
						return false;
					}
				}
				y = delta * (xLeft - x1) + y1;
				if (y >= yTop && y < yBottom)
				{
					return true;
				}

				y = delta * (xRight - x1) + y1;
				if (y >= yTop && y < yBottom)
				{
					return true;
				}

				return false;
			}
			else
			{
				delta = (x2 - x1) / (y2 - y1);
				if (y2 > y1)
				{
					if (yBottom < y1 || yTop >= y2)
					{
						return false;
					}
				}
				else
				{
					if (yBottom < y2 || yTop >= y1)
					{
						return false;
					}
				}
				x = delta * (yTop - y1) + x1;
				if (x >= xLeft && x < xRight)
				{
					return true;
				}

				x = delta * (yTop - y1) + x1;
				if (x >= xLeft && x < xRight)
				{
					return true;
				}

				return false;
			}
		},

